import React, { useState, useEffect } from "react";
import { withRouter } from "react-router-dom";
import { loadModels, getFullFaceDescription, createMatcher } from "../api/face";

const testImg = require("../img/anhthe.jpg");
const JSON_PROFILE = require("../descriptors/bnk48.json");

const INIT_STATE = {
  imageURL: testImg,
  fullDesc: null,
  detections: null,
  descriptors: null,
  match: null,
};

function Photo(props) {
  const [state, setState] = useState({
    ...INIT_STATE,
    faceMatcher: null,
    loading: false,
  });
  let temp = INIT_STATE;
  useEffect(() => {
    initialize();
  }, [state.imageURL]);

  async function initialize() {
    await loadModels();
    const faceMatcher = await createMatcher(JSON_PROFILE);
    
    setState((prevState) => ({
      ...prevState,
      faceMatcher: faceMatcher,
    }));
    await handleImage(state.imageURL);
  }

  const handleImage = async (image = state.imageURL) => {
    const fullDesc = await getFullFaceDescription(image);
    const faceMatcher = await createMatcher(JSON_PROFILE);
    if (fullDesc && fullDesc.length > 0) {
      setState((prevState) => ({
        ...prevState,
        fullDesc: fullDesc,
        detections: fullDesc.map((fd) => fd.detection),
        descriptors: fullDesc.map((fd) => fd.descriptor),
        faceMatcher: faceMatcher
      }));
    }

    if (state.descriptors && state.faceMatcher) {
      try{
        const match = state.descriptors.map((descriptor) =>
          state.faceMatcher.findBestMatch(descriptor)
        );
        setState((prevState) => ({ ...prevState, match }));
      }
      catch{
        console.log('error');
      }
    }
  };

  const handleFileChange = async (event) => {
    resetState();
    const imageURL = URL.createObjectURL(event.target.files[0]);
    setState((prevState) => ({ ...prevState, imageURL, loading: true }));
    handleImage();
  };

  const resetState = () => {
    setState({ ...INIT_STATE });
  };

  const { imageURL, detections, match } = state;

  let drawBox = null;
  if (detections && detections.length > 0) {
    drawBox = detections.map((detection, i) => {
      const _H = detection.box.height;
      const _W = detection.box.width;
      const _X = detection.box._x;
      const _Y = detection.box._y;

      return (
        <div key={i}>
          <div
            style={{
              position: "absolute",
              border: "solid",
              borderColor: "blue",
              height: _H,
              width: _W,
              transform: `translate(${_X}px,${_Y}px)`,
            }}
          >
            {match ? (
              <p
                style={{
                  backgroundColor: "blue",
                  border: "solid",
                  borderColor: "blue",
                  width: _W,
                  marginTop: 0,
                  color: "#fff",
                  transform: `translate(-3px,${_H}px)`,
                }}
              >
                {match[i]._label}
              </p>
            ) : null}
          </div>
        </div>
      );
    });
  }

  return (
    <div>
      <input
        id="myFileUpload"
        type="file"
        onChange={handleFileChange}
        accept=".jpg, .jpeg, .png"
      />
      <div style={{ position: "relative" }}>
        <div style={{ position: "absolute" }}>
          <img src={imageURL} alt="imageURL" />
        </div>
        {drawBox ? drawBox : null}
      </div>
    </div>
  );
}

export default Photo
